mapper.select:
SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM BOARD 
					ORDER BY idx ASC 
					) A
			)
	WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE BOARD
SET TITLE = #{title}, 
       WRITER = #{writer},
       CONTENTS = #{contents}
WHERE IDX = #{idx}
